div.div:
    {% for coin in trade_coins %}
      {% usd_value coin.coin_symbol 1 as balance %}



{{coin.coin_name}} 
{{balance}} 
      {% block script-js %}

          var type = "buy";
          $(".buy-tab").on("click" , function (){
            type = "buy";
             $(".lower-verb").html("buy");
             $(".upper-verb").html("Buy");
             $(".sell-tab").removeClass("text-primary");
             $(".buy-tab").addClass("text-primary");
             $(".sell-active").removeClass("active");
             $(".buy-active").addClass("active");
          });
          $(".sell-tab").on("click" , function (){
             type = "sell";
             $(".lower-verb").html("sell");
             $(".upper-verb").html("Sell");
             $(".sell-tab").addClass("text-primary");
             $(".buy-tab").removeClass("text-primary");
             $(".sell-active").addClass("active");
             $(".buy-active").removeClass("active");
          }); 
          $("#coin{{coin.id}}").on("click" , function(){
           if(type == "buy"){
              $("body").load("/havwis/buy/{{coin.id}}/");
           }
           if(type == "sell"){
             $("body").load("/havwis/sell/{{coin.coin_name}}/");
           }
          });

      {% endblock %}
    {% endfor %}
